        private async void Delete(Guid id)
        {
            bool confirmed = await JS.InvokeAsync<bool>("confirm", "Are you sure  you want to delete this record?");
            if (confirmed)
            {
                var response = await Http.DeleteAsync($"user/{id}");
            }

            Refresh();
